import React, { Fragment } from "react";
import {isAuth, signout} from "./paths/helpers"

import {Link, withRouter} from "react-router-dom";
function Navbar({match, history}) {

    console.log(history.location.pathname)

    const isActive = path =>{
        if(history.location.pathname === path) {
            return {color: "#000"}
        }else {
            return {color: "#f05454"}
        }

        
    }

    const navy = () =>{
        return (
            <div>
    <Link  style={isActive("/")} to="/">home</Link>
    
            {!isAuth() && (
                <Fragment >
                <Link className="nav-link" style={isActive("/signin")} to="signin">signin</Link>
                <Link className="nav-link" style={isActive("/signup")} to="signup">signup</Link>  
                </Fragment>
            )}

            {isAuth() && (
                <span className="nav-link" onClick={()=>{
                    signout(()=>{
                        history.push("/")
                    })
                }}>Signout</span>
            )}
        
            </div>
        );
    }

    return <div>
    {navy()}
       
    </div>
}

export default withRouter(Navbar);
