import React, { useState } from "react";
import {Link, Redirect} from "react-router-dom";
import axios from "axios";
import {authenticate, isAuth} from "./helpers"
import {ToastContainer, toast} from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css'

function Signin(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [buttonText, setButtonText] = useState("Submit");

      async  function handelSubmit(e){
            e.preventDefault();
             setButtonText("Submitting");
            
                 const registerData = {
                    email, password
                 };
                 await axios.post(`${process.env.REACT_APP_API}/login`, registerData)
                 .then(res=>{
                     authenticate(res, ()=>{
                        setEmail("")
                        setPassword("")
                        setButtonText("Submitted");
                        toast.success(`hey, ${res.data.user.name}`)
                     });
               
                 }) 
                 .catch(error=>{
                    setButtonText("Submit");
                    toast.error(error.response.data.errorMessage)

                 })
            
           
        }
  
    return <div>
         <nav>
      <div className="logo">
          <h1><Link className="logo"  to="/">Montdls</Link></h1>
    
          </div>
          <div className="links">
      <Link className="nav-link"   to="/signup" class="btn-b"  >Sign un</Link>
      </div>
      </nav>

      <div className="signup-form">
        <ToastContainer />

        <div className="signup-form-heading">
            <h1>Log in</h1>
            <p>Welcome back!</p>
           </div> 

        {isAuth()? <Redirect to="/" />: null }
        <form className="form" onSubmit={handelSubmit}>
      
        <input className="form-control" onChange={(e)=> setEmail(e.target.value)} value={email} type="email" placeholder="email"></input>
        <input className="form-control" type="password" onChange={(e)=> setPassword(e.target.value)} value={password} placeholder="password"></input>
        <button type="submit" style={ {margin:" 10px auto"}}  className="form-control btn-b">{buttonText}</button>

        </form>
        <div className="signup-p">
        <p>OR</p>
        <button className="form-control btn-red"><i class="fab fa-google"></i> Log in with fa-google</button>
        <button disabled className="form-control btn-blue"><i class="fab fa-facebook-f"></i> Log in with Facebook</button>
       </div>
    </div>
    </div>
    
    
}

export default Signin;
