import React, { useState } from "react";
import {Link, Redirect} from "react-router-dom";
import axios from "axios";
import {isAuth} from "./helpers";
import styles from "./forms.module.css";
function Signup(){
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [buttonText, setButtonText] = useState("Submit");
    const [hideBtn, setHideBtn] = useState(false);
    const [errMsgName, setErrMsgName] = useState("");
    const [errMsgEmail, setErrMsgEmail] = useState("");
    const [errMsgPassword, setErrMsgPassword] = useState("");
    const [emnB, setEmnB] = useState(false);
    const [emeB, setEmeB] = useState(false);
    const [empB, setEmpB] = useState(false);

      async  function handelSubmit(e){
            e.preventDefault();
             setHideBtn(true);
             setEmnB(false)
            
                 const registerData = {
                     name, email, password
                 };
                 await axios.post(`${process.env.REACT_APP_API}/signup`, registerData)
                 .then(res=>{
                    setName("")
                    setEmail("")
                    setPassword("")
                    setButtonText("Submitted");
                    setHideBtn(false);
                    setEmnB(false)
                    setEmpB(false)
                    setEmeB(false)
                    toast.success(res.data.message)
                 }) 
                 .catch(error=>{
                    setButtonText("Submit");
                    setEmnB(error.response.data.name)
                    setEmeB(error.response.data.email)
                    setEmpB(error.response.data.password)
                    setHideBtn(false);
                    setErrMsgName(error.response.data.errorMessageName)
                    setErrMsgEmail(error.response.data.errorMessageEmail)
                    setErrMsgPassword(error.response.data.errorMessagePassword)

                 })
                 
            
           
        }
  
    return <div>
    <ToastContainer />

 

      
    

    
    {isAuth()? <Redirect to="/" />: null }

        <div className={styles.sign_up}>
        <h1>Create an account</h1>
        <div className={styles.auth_methods}>
            <div className={styles.google}> 
            <img src="./images/google.png" />  Continue with Google
            </div>
            <div className={styles.facebook}>
            <img src="./images/facebook.png" />

            </div>
        </div>
        <div className={styles.or_methods}>
        <div className={styles.or_methods_div}></div>
        <h3>or</h3>
        <div className={styles.or_methods_div}></div>
        </div>
        
        <form className={styles.form} onSubmit={handelSubmit}>

        <input  type="text" placeholder="Enter your name..." onChange={(e)=> setName(e.target.value)} value={name} class={emnB ? styles.red_input : null} autocomplete="off" />
            <div class={emnB? null: styles.hide_content}><p><i class="fas fa-exclamation-triangle"></i> {errMsgName}</p></div>

            <input  type="email" placeholder="Enter your email..." onChange={(e)=> setEmail(e.target.value)} value={email} class={emeB ? styles.red_input : null} autocomplete="off" />
            <div class={emeB? null: styles.hide_content}><p><i class="fas fa-exclamation-triangle"></i> {errMsgEmail}</p></div>

            <input  type="password" class="red-input1" placeholder="Enter your password..." onChange={(e)=> setPassword(e.target.value)} class={empB ? styles.red_input : null} value={password} autocomplete="off" />
            <div class={empB? null: styles.hide_content}><p><i class="fas fa-exclamation-triangle"></i> {errMsgPassword}</p></div>

            <div class="btn-container">
                <div class={hideBtn? styles.loading_btn : null}></div>
                <div class={hideBtn? styles.loading_btn2  : null}></div>

                    <button type="submit"  class={hideBtn? styles.button_hide : null}>{buttonText}</button>
            </div>

        </form>
        <p>or <Link to="/signin">Log in</Link> </p>

    <p>By clicking submit you're agreeing to our use of cookie and privacy policies.</p>
        </div>
  
    </div>
    
    
    
    
}

export default Signup;
