import React, { useEffect, useState } from "react";
import axios from "axios";
import styles from "./taskStyles.module.css";
import Navbar from "../navbar"
import { isAuth} from "./helpers"


function Tasks(){

    const [tasks, setTasks] = useState("");
    const [data, setData] = useState([]);
    console.log(isAuth())
    async function handelSubmit(e){
        setTasks("");
        e.preventDefault();
         axios.post(`${process.env.REACT_APP_API}/task`, {tasks: tasks})
         .then(res => {
            // console.log(res);
            // console.log(res.data);
          })

    }

    useEffect(()=>{
      axios.get(`${process.env.REACT_APP_API}/task`, {tasks: tasks} ).then((response)=>{
        setData(response.data)
      }).catch((err)=>{alert(err)})
    });



    return <div className={styles.bg_dark}>

     <div className={styles.menu_bar}>
            <span></span>
            <span></span>
            <span></span>
        </div>
        <div className={styles.menu+" "+styles.hide_for_mobile}>


            <div  className={styles.menu_point}>

            </div>
                <div className={styles.user_profile}>
                    <div className={styles.body_container}>
                        <div className={styles.head}>
                        
                        </div>
                        <div className={styles.body}>
                            
                        </div>
                    </div>
                </div>
                <div className={styles.greeting}>
                  <h2>Goog morning,<br></br> Ouhoy!</h2>
                </div>
                <div className={styles.hide_tdls_bg}>
                    <h2>More features are coming soon <i className="fas fa-heart"></i> </h2>
                </div>
                <div className={styles.sign_out}>
                    <Navbar />
                </div>
           
          
          
           

             

        </div>
<div className={styles.date}>
            <h1>My Day</h1>
        </div>
    <form onSubmit={handelSubmit}>
      <div className={styles.add_task}>
      <input className={styles.textInput} onChange={(e)=> setTasks(e.target.value)} value={tasks} type="text" />
      </div>

      {data.map((contents, index)=>{ return <div key={index+ "div"} className={styles.lable_div}>
      <input key={index+ "checkbox"} type="checkbox"></input>
      <label key={index} >{contents.content}</label>
      </div>
      })}

      </form>

    </div>
}

export default Tasks;
