import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../navbar";
// import styles from "./taskStyles.module.css";

function Tasks(){

    const [tasks, setTasks] = useState("");
    const [data, setData] = useState([]);

    async function handelSubmit(e){
        setTasks("");
        e.preventDefault();
         axios.post(`${process.env.REACT_APP_API}/task`, {tasks: tasks})
         .then(res => {
            console.log(res);
            console.log(res.data);
          })

    }

    useEffect(()=>{
      axios.get(`${process.env.REACT_APP_API}/task`, {tasks: tasks} ).then((response)=>{
        setData(response.data)
      }).catch((err)=>{alert(err)})
    },[data]);



    return <div >
    <Navbar />
    
    <form onSubmit={handelSubmit}>
      <div className={styles.add_task}>
      <input className={styles.textInput} onChange={(e)=> setTasks(e.target.value)} value={tasks} type="text" />
      </div>

      {data.map(contents=>{ return <div className={styles.lable_div}>
      <input  type="checkbox"></input>
      <label >{contents.content}</label>
      </div>
      })}

      </form>

    </div>
}

export default Tasks;
