import React, { useState } from "react";
import {Link, Redirect} from "react-router-dom";
import axios from "axios";
import {isAuth} from "./helpers"

import {ToastContainer, toast} from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css'

function Signup(){
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [buttonText, setButtonText] = useState("Submit");
    const [hideBtn, setHideBtn] = useState(false);
    const [errMsgName, setErrMsgName] = useState("");
    const [emnB, setEmnB] = useState(false);

      async  function handelSubmit(e){
            e.preventDefault();
             setButtonText("Submitting");
             setHideBtn(true);
             setEmnB(false)
            
                 const registerData = {
                     name, email, password
                 };
                 await axios.post(`${process.env.REACT_APP_API}/signup`, registerData)
                 .then(res=>{
                    setName("")
                    setEmail("")
                    setPassword("")
                    setButtonText("Submitted");
                    setHideBtn(false);
                    setEmnB(false)
                    toast.success(res.data.message)
                 }) 
                 .catch(error=>{
                    setButtonText("Submit");
                    setEmnB(error.response.data.name)
                    setHideBtn(false);
                    toast.error(error.response.data.errorMessage)
                    setErrMsgName(error.response.data.errorMessageName)

                 })
                 
            
           
        }
  
    return <div>
    <ToastContainer />

 

      
    

    
    {isAuth()? <Redirect to="/" />: null }

        <div className="sign-up">
        <h1>Create an account</h1>
        <div className="auth-methods">
            <div className="google"><i className="fab fa-google"></i> Continue with Google</div>
            <div className="facebook"><i className="fab fa-facebook-f"></i> Continue with Facebook</div>
        </div>
        <hr/>
        
        <form className="form" onSubmit={handelSubmit}>

        <input  type="text" placeholder="Enter your name..." onChange={(e)=> setName(e.target.value)} value={name} class={emnB ? "red-input" : null} autocomplete="off" />
            <div class={emnB? null: "hide-content"}><p><i class="fas fa-exclamation-triangle"></i> {errMsgName}</p></div>

            <input  type="email" placeholder="Enter your email..." onChange={(e)=> setEmail(e.target.value)} value={email} class="red-input1" autocomplete="off" />
            <div class="iput-problem hide-content"><p><i class="fas fa-exclamation-triangle"></i> {errMsgName}</p></div>

            <input  type="password" class="red-input1" placeholder="Enter your password..." onChange={(e)=> setPassword(e.target.value)} value={password} />
            <div class="iput-problem hide-content"><p><i class="fas fa-exclamation-triangle"></i> Please put a password with at least 8 characters.</p></div>

            <div class="btn-container">
                <div class={hideBtn? "loading-btn" : null}></div>

                    <button type="submit"  class={hideBtn?"button-hide" : null}>{buttonText}</button>
            </div>

        </form>
        <p>or <a href="/">Log in</a></p>

    <p>By clicking submit you're agreeing to our use of cookie and privacy policies.</p>
        </div>
  
    </div>
    
    
    
    
}

export default Signup;
