import React, { useState } from "react";
import axios from "axios";
import {Link, Redirect} from "react-router-dom";
import styles from "../styles/forms.module.css";

function ForgotPassword(){

    const [email, setEmail] = useState("")

    function handelSubmit(e) {
        e.preventDefault();
        const data = {email};
        axios
        .post("http://localhost:5000/forgot-password", data)
        .then(<Redirect to="/signin"/>)
        .catch(err=>{
            console.log(err)
        })
    }
    return <div>
        <div className={styles.sign_up}>
    <h1>Forgot password</h1>
    <form  className={styles.form} onSubmit={handelSubmit}>
    <input type="password" placeholder="Enter your new password..." onChange={(e)=> setEmail(e.target.value)}  value={email} />
    <div className={styles.btn_container}>
                    <button type="submit" >Submit</button>
            </div>
    </form>
    <p className={styles.sm_ps}>By clicking submit you're agreeing to our use of cookie and privacy policies.</p>

    </div>
    </div>
}

export default ForgotPassword;
