import React, { useState } from "react";
import {Link, Redirect} from "react-router-dom";
import axios from "axios";
import {isAuth} from "./helpers"

import {ToastContainer, toast} from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css'

function Signup(){
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [buttonText, setButtonText] = useState("Submit");

      async  function handelSubmit(e){
            e.preventDefault();
             setButtonText("Submitting");
            
                 const registerData = {
                     name, email, password
                 };
                 await axios.post(`${process.env.REACT_APP_API}/signup`, registerData)
                 .then(res=>{
                    setName("")
                    setEmail("")
                    setPassword("")
                    setButtonText("Submitted");
                    toast.success(res.data.message)
                 }) 
                 .catch(error=>{
                    setButtonText("Submit");
                    toast.error(error.response.data.errorMessage)

                 })
                 
            
           
        }
  
    return <div>
    <ToastContainer />

    <nav>
      <div className="logo">
          <h1><Link className="logo"  to="/">Montdls</Link></h1>
    
          </div>
          <div className="links">
      <Link className="nav-link"   to="/signin" class="btn-login"  >Sign in</Link>
      </div>
      </nav>

      
    <div className="signup-form">
    

    <div className="signup-form-heading">
            <h1>Sign Up</h1>     
        </div>
    {isAuth()? <Redirect to="/" />: null }

    <form class="form" onSubmit={handelSubmit}>
    <div className="form-group">
    <input className="form-control" onChange={(e)=> setName(e.target.value)} value={name} placeholder="name"></input>
       
    </div>
    <div className="form-group">
    <input className="form-control" onChange={(e)=> setEmail(e.target.value)} value={email} type="email" placeholder="email"></input>

    </div>
    <div className="form-group">
    <input className="form-control" type="password" onChange={(e)=> setPassword(e.target.value)} value={password} placeholder="password"></input>

    </div>
    <button type="submit" className="form-control btn-b btn-green">{buttonText}</button>

    </form>

    <div class="signup-p">
        <p>OR</p>
        <button className="form-control btn-red"><i class="fab fa-google"></i> Sign up with Google</button>
        <button disabled className="form-control btn-blue"><i class="fab fa-facebook-f"></i> Sign up with Facebook</button>
       </div>

</div>

    </div>
    
    
    
    
}

export default Signup;
