import React, {useState, useEffect} from "react"
import Navbar from "../navbar"
import Axios from "axios";
import {isAuth} from "./helpers"
import axios from "axios";


function TasksHome(){

    const [content, setContent] = useState("");
    const [tasks, setTasks] = useState([]);
    const [CheckedItem , setCheckedItem] = useState([]);
    const [isPoint, setIspoint] = useState(false);
    const [isPointf , setIspointf] = useState(false);
    const [isPoints , setIspoints] = useState(false);
    const [isPoint4 , setIspoint4] = useState(false);


       const user = isAuth.email;
     axios.post(`${process.env.REACT_APP_API}/task`, user )

     



    function handelSubmit (e) {
        e.preventDefault();
      }
      function handleChange(event){
          const newInput = event.target.value;
          setContent(newInput)
      }
     

function saveTask(){
    Axios.post("http://localhost:3001/" , {content : content})
    setContent("")
}

useEffect(()=>{
    Axios.get("http://localhost:3001/" , {content : content}).then((response)=>{
        setTasks(response.data)
    }).catch((error)=>{
      alert(error)
    })

  } , [tasks])

useEffect(()=>{
    Axios.get("http://localhost:3001/CheckedItem" , {content : content}).then((response)=>{
        setCheckedItem(response.data)
    }).catch((error)=>{
      alert(error)
    })

  } , [CheckedItem])


  const deleteTask = (id)=> {
    Axios.delete(`http://localhost:3001/${id}` , [])
   
  }
  const updateTask = (id)=>{
    const newTask = prompt("Update your task")
    Axios.put("http://localhost:3001/" , {newTask:newTask, id:id} )
  }
  const checkTask = (id)=> {
    Axios.delete(`http://localhost:3001/checked/${id}` , [])
   
  }
  const deleleChecked = (id)=> {
    Axios.delete(`http://localhost:3001/deletechecked/${id}` , [])
   
  }
return <div className="body2">
<Navbar/> 
  <div onClick={()=>{ setIspoint4(!isPoint4)}} className="menu-bar cursor-pointer">
            <span></span>
            <span></span>
            <span></span>
        </div>
        <div className={isPoint4? "menu" : "menu hidden"}>


            <div onClick={()=>{ setIspoint4(!isPoint4)}} className="menu-point">

            </div>
                <div className="user-profile">
                    <div className="body-container">
                        <div className="head">
                        
                        </div>
                        <div className="body">
                            
                        </div>
                    </div>
                </div>
                <div className="greeting">
                  <h2>Goog morning,<br></br>{isAuth().name}</h2>
                </div>
                <div className="hide-tdls-bg">
                    <h2>More features are coming soon <i className="fas fa-heart"></i></h2>
                </div>
                <div className="sign-out">
                    <p>Sign Out <i className="material-icons">logout</i></p>
                </div>
           
          
          
           

             

        </div>
<div className="date">
            <h1>My Day</h1>
        </div>
 <div  className="tasks">


 <form onSubmit={handelSubmit}>
       <div className="add-task">
       <input type="text" autoComplete="off" onChange={handleChange} value={content} className="textInput" placeholder="Add a task"></input>
       </div>
       <button type="submit" className="hidden" onClick={saveTask} ></button>
       </form>

       
       {tasks.map((task , index)=>{

         return <div className="lable-div" >
         <div  onClick={()=>{ setIspoint(!isPoint)}} id="links-point" className="manage-links-point"></div> 
                          <div id={index} key={index} className={isPoint? "manage-links1" : "hidden" }>
                            <div id="manage-links" className="manage-links">
                            <button  onClick={()=>{deleteTask(task._id)}} ><i className="far fa-trash-alt"></i></button>
                                {/* <button onClick={()=>{updateTask(task._id)}} name="edit"><i className="far fa-edit"></i></button> */}
                    
                            </div>
                        </div>
                        
         <input id={index} onChange={()=>{checkTask(task._id)}}  type="checkbox"></input>
         <label >{task.content}</label>
         </div>
       })}
       <div  onClick={()=>{ setIspointf(!isPointf)}} className="hide-lable-point">
       <p><i className={isPointf?"fas fa-caret-right rotate":"fas fa-caret-right "}></i>  Checked</p>
       </div>
       <div className={isPointf?"hide-lable": "hidden"}>
       
       {CheckedItem.map((checked , index)=>{
           return (
            <div className="lable-div">
       <div id="links-point"  onClick={()=>{ setIspoints(!isPoints)}} className="manage-links-point"></div> 
            <div className={isPoints? "manage-links1" :"hidden" }>

            <div className="manage-links">
            <button  onClick={()=>{deleleChecked(checked._id)}} ><i className="far fa-trash-alt"></i></button>

            </div>

            </div>
            <input checked id={index}  type="checkbox"></input>
         <label className="checked"  >{checked.content}</label>

       </div>
               );
                     
    })}
       </div>




       </div>

       

    </div>
}

export default TasksHome;
