import React, { useState } from "react";
import axios from "axios";
import styles from "../styles/forms.module.css";


function ResetPassword({match}){
    const [password, setPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    async  function handelSubmit(e){
        e.preventDefault();
        const token = match.params.token;
        console.log(token)
        const data = {password,newPassword,token }
        await axios.put(`http://localhost:5000/reset-password`,data)
        .then( res =>{
            res.setNewPassword("");
            res.setPassword("");
        }
        )
        .catch(err=>{
            console.log(err)
        })
 
    }
    return <div className={styles.sign_up}>
    <form  className={styles.form} onSubmit={handelSubmit}>
    <input  type="password" placeholder="Enter your new password..." onChange={(e)=> setPassword(e.target.value)}  value={password} />
    <input  type="password" placeholder="Enter your new password..." onChange={(e)=> setNewPassword(e.target.value)}  value={newPassword} />

    <div className={styles.btn_container}>
                    <button type="submit" >Submit</button>
            </div>
    </form>
    </div>
}

export default ResetPassword;
