import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../navbar";
import styles from "./taskStyles.module.css";
import {signout} from "./paths/helpers"


function Tasks({history}){

    const [tasks, setTasks] = useState("");
    const [data, setData] = useState([]);

    async function handelSubmit(e){
        setTasks("");
        e.preventDefault();
         axios.post(`${process.env.REACT_APP_API}/task`, {tasks: tasks})
         .then(res => {
            console.log(res);
            console.log(res.data);
          })

    }

    useEffect(()=>{
      axios.get(`${process.env.REACT_APP_API}/task`, {tasks: tasks} ).then((response)=>{
        setData(response.data)
      }).catch((err)=>{alert(err)})
    },[data]);



    return <div >

    <Navbar />
     <div className="menu-bar">
            <span></span>
            <span></span>
            <span></span>
        </div>
        <div className="menu hide-for-mobile">


            <div  className="menu-point">

            </div>
                <div className="user-profile">
                    <div className="body-container">
                        <div className="head">
                        
                        </div>
                        <div className="body">
                            
                        </div>
                    </div>
                </div>
                <div className="greeting">
                  <h2>Goog morning,<br></br> Ouhoy!</h2>
                </div>
                <div className="hide-tdls-bg">
                    <h2>More features are coming soon <i className="fas fa-heart"></i></h2>
                </div>
                <div className="sign-out">
                    <p onClick={()=>{
                    signout(()=>{
                        history.push("/")
                    })
                }}>Sign Out <i className="material-icons">logout</i></p>
                </div>
           
          
          
           

             

        </div>
<div className="date">
            <h1>My Day</h1>
        </div>
    <form onSubmit={handelSubmit}>
      <div className={styles.add_task}>
      <input className={styles.textInput} onChange={(e)=> setTasks(e.target.value)} value={tasks} type="text" />
      </div>

      {data.map(contents=>{ return <div className={styles.lable_div}>
      <input  type="checkbox"></input>
      <label >{contents.content}</label>
      </div>
      })}

      </form>

    </div>
}

export default Tasks;
