import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../navbar";

function Tasks(){

    const [tasks, setTasks] = useState("");
    const [content, setContent] = useState("");

    async function handelSubmit(e){
        setTasks("");
        e.preventDefault();
       await  axios.post(`${process.env.REACT_APP_API}/task`, {tasks: tasks})
         .then(res => {
            console.log(res);
            console.log(res.data);
          })

    }

    useEffect(()=>{
      axios.get(`${process.env.REACT_APP_API}/task`,{content:content} ).then((response)=>{
        setContent(response.data)
      }).catch((err)=>{alert(err)})
    },[]);
    console.log(content)


    return <div className="body2">
    <Navbar />
    
    <form className="form" onSubmit={handelSubmit}>
      <div className="add-task">
      <input className="textInput" onChange={(e)=> setTasks(e.target.value)} value={tasks} type="text" />
      </div>

      {content.map((conten, index)=>{ return <div className="lable-div">
      <input id={index}  type="checkbox"></input>
      <label >{conten.content}</label>


      </div>
      })}

      </form>

    </div>
}

export default Tasks;
