import React, { useState } from "react";
import {Link, Redirect} from "react-router-dom";
import axios from "axios";
import {authenticate, isAuth} from "./helpers"
import {ToastContainer, toast} from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css'

function Signin(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [buttonText, setButtonText] = useState("Submit");
    const [hideBtn, setHideBtn] = useState(false);
    const [errMsgEmail, setErrMsgEmail] = useState("");
    const [errMsgPassword, setErrMsgPassword] = useState("");
    const [emeB, setEmeB] = useState(false);
    const [empB, setEmpB] = useState(false);

      async  function handelSubmit(e){
            e.preventDefault();
             setButtonText("Submitting");
             setHideBtn(true);

                 const registerData = {
                    email, password
                 };
                 await axios.post(`${process.env.REACT_APP_API}/login`, registerData)
                 .then(res=>{
                     authenticate(res, ()=>{
                        setEmail("")
                        setPassword("")
                        setButtonText("Submitted");
                        setHideBtn(false);
                        setEmpB(false);
                        setEmeB(false);
                        
                     });
               
                 }) 
                 .catch(error=>{
                    setButtonText("Submit");
                    setEmeB(error.response.data.email);
                    setEmpB(error.response.data.password);
                    console.log(error.data)
                    setHideBtn(false);
                    setErrMsgEmail(error.response.data.errorMessageEmail);
                    setErrMsgPassword(error.response.data.errorMessagePassword);

                 })
            
           
        }
  
    return <div>
  
        
    {isAuth()? <Redirect to="/" />: null }

<div className="sign-up">
<h1>Sign In</h1>
<div className="auth-methods">
    <div className="google"><i className="fab fa-google"></i> Continue with Google</div>
    <div className="facebook"><i className="fab fa-facebook-f"></i> Continue with Facebook</div>
</div>
<hr/>

<form className="form" onSubmit={handelSubmit}>

    <input  type="email" placeholder="Enter your email..." onChange={(e)=> setEmail(e.target.value)} value={email} class={emeB ? "red-input" : null} autocomplete="off" />
    <div class={emeB? null: "hide-content"}><p><i class="fas fa-exclamation-triangle"></i> {errMsgEmail}</p></div>

    <input  type="password" class="red-input1" placeholder="Enter your password..." onChange={(e)=> setPassword(e.target.value)} class={empB ? "red-input" : null} value={password} />
    <div class={empB? null: "hide-content"}><p><i class="fas fa-exclamation-triangle"></i> {errMsgPassword}</p></div>

    <div class="btn-container">
        <div class={hideBtn? "loading-btn2" : null}></div>
        <div class={hideBtn? "loading-btn" : null}></div>

            <button type="submit"  class={hideBtn?"button-hide" : null}>{buttonText}</button>
    </div>

</form>
<p>No account? <a href="/">Sign Up</a></p>

<p>By clicking submit you're agreeing to our use of cookie and privacy policies.</p>
</div>

      
    </div>
    
    
}

export default Signin;
