import React, { useState } from "react";
import {Link, Redirect} from "react-router-dom";
import axios from "axios";
import {ToastContainer, toast} from "react-toastify";
import 'react-toastify/dist/ReactToastify.min.css'

function Signup(){
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [buttonText, setButtonText] = useState("Submit");

      async  function handelSubmit(e){
            e.preventDefault();
             setButtonText("Submitting");
            
                 const registerData = {
                     name, email, password
                 };
                 await axios.post(`${process.env.REACT_APP_API}/signup`, registerData)
                 .then(res=>{
                    setName("")
                    setEmail("")
                    setPassword("")
                    setButtonText("Submitted");
                    toast.success(res.data.message)
                 }) .catch(error => {
                    console.log(error.response.data.errorMessage);
                    toast.error(error.response.data.errorMessage)
                    setButtonText("Submit");
   
                })
                 
            
           
        }
  
    return <div>
        <ToastContainer />

        <h1>Signup</h1>

        <form onSubmit={handelSubmit}>
        <div className="form-group">
        <input className="form-control" onChange={(e)=> setName(e.target.value)} value={name} placeholder="name"></input>
           
        </div>
        <div className="form-group">
        <input className="form-control" onChange={(e)=> setEmail(e.target.value)} value={email} type="email" placeholder="email"></input>

        </div>
        <div className="form-group">
        <input className="form-control" type="password" onChange={(e)=> setPassword(e.target.value)} value={password} placeholder="password"></input>

        </div>
        <button type="submit" className="btn btn-primary">{buttonText}</button>

        </form>

    </div>
}

export default Signup;
